import React from 'react';

const Card = ({details}) => (
  <div className="card">
  <div className="image" value={details.image}/>
      <div className="recette">
          <h2> {details.nom} </h2>
          <p> {details.instructions} </p>
      </div>
  </div>
);

export default Card;
