import React  from 'react';

const Header = ({pseudo}) =>  {
  const formatPseudo = pseudo => /[aeiouy]/i.test(pseudo[0])  ? `d'
    ${pseudo}` : `de ${pseudo}`

    return (
      <ColorContext.Consumer>
      <header>
      <h1>La boîte à recettes {formatPseudo(pseudo)}</h1>
      </header>
      </ColorContext.Consumer>
    );
}

export default Header;
