import React from 'react';

const Card = ({details}) => (
  <div className="card">
    <div className="image">
    <img src={require(`../img/${details.image}`)} alt={details.nom}/>
    </div>
      <div className="recette">
          <h2> {details.nom} </h2>
          <p>{details.instructions}</p>
      </div>
  </div>
);

export default Card;
