import React, { Component } from 'react';

class AddRecette extends Component {
  state = {
    nom:"",
    image: "",
    ingredients: "",
    instructions: ""
  }
  render() {
    return (
      <div className="card">
      <form className="admin-form add-recette">
          <input value={this.state.nom}  name="nom" type="text" placeholder="nom de la recette"/>
          <input value={this.state.image} name="image" type="text" placeholder="nom de l'image"/>
          <textarea value={this.state.nom} name="ingredients" rows="3" placeholder="liste des ingredients"/>
          <textarea value={this.state.nom} name="instructions" rows="15" placeholder="liste des instructions"/>
      </form>
      </div>
    );
  }

}

export default AddRecette;
